import{_ as n,r as o,o as t,c,a as e,b as r,F as p,d as a,e as i}from"./app.07e6cbcb.js";var l="/sicp-js/assets/ch1-Z-G-1.23277da0.svg";const d={},u=e("h1",{id:"_1-1-3-\u8BA1\u7B97\u8FD0\u7B97\u7B26\u7EC4\u5408",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-1-3-\u8BA1\u7B97\u8FD0\u7B97\u7B26\u7EC4\u5408","aria-hidden":"true"},"#"),a(" 1.1.3 \u8BA1\u7B97\u8FD0\u7B97\u7B26\u7EC4\u5408")],-1),m={href:"https://sourceacademy.org/sicpjs/1.1.3",target:"_blank",rel:"noopener noreferrer"},_=a("Original"),h=i(`<p><strong>TODO:</strong> \u597D\u591A\u7FFB\u4E0D\u51FA\u6765, \u9700\u8981\u91CD\u65B0 review</p><p>\u672C\u7AE0\u6211\u4EEC\u7684\u76EE\u7684\u662Fto isolate issues about thinking procedurally.</p><p>\u8981\u8BA1\u7B97\u8FD0\u7B97\u7B26\u7EC4\u5408, \u9996\u5148\u6267\u884C\u4EE5\u4E0B\u64CD\u4F5C:</p><ol><li>\u8BC4\u4F30\u8FD0\u7B97\u5143\u8868\u8FBE\u5F0F\u7684\u7EC4\u5408</li></ol><p>\u5C3D\u7BA1\u89C4\u5219\u5341\u5206\u7B80\u5355, \u4F46\u662F\u4E5F\u4F53\u73B0\u4E86\u901A\u7528\u8FC7\u7A0B\u7684\u91CD\u8981\u4E4B\u5904. First, observe that the first step dictates that in order to accomplish the evaluation process for a combination we must first perform the evaluation process on each operand of the combination. \u5176\u6B21, \u8BA1\u7B97\u89C4\u5219\u672C\u8D28\u4E0A\u662F <em>\u9012\u5F52</em> \u7684, \u7B80\u5355\u6765\u8BF4\u5C31\u662F\u9700\u8981\u8C03\u7528\u81EA\u5DF1.</p><p>\u9012\u5F52\u7684\u6982\u5FF5\u80FD\u591F\u5F88\u7B80\u6D01\u5730\u8868\u8FBE\u76F8\u5E94\u7684\u542B\u4E49, \u5728\u5D4C\u5957\u5F88\u6DF1\u7684\u7EC4\u5408\u4E2D, \u4F1A\u88AB\u770B\u505A\u662F\u4E00\u4E2A\u590D\u6742\u6B65\u9AA4. \u6BD4\u5982\u8BA1\u7B97\u5982\u4E0B\u8868\u8FBE\u5F0F:</p><div class="language-javascript ext-js line-numbers-mode"><pre class="language-javascript"><code><span class="token punctuation">(</span><span class="token number">2</span> <span class="token operator">+</span> <span class="token number">4</span> <span class="token operator">*</span> <span class="token number">6</span><span class="token punctuation">)</span> <span class="token operator">*</span> <span class="token punctuation">(</span><span class="token number">3</span> <span class="token operator">+</span> <span class="token number">12</span><span class="token punctuation">)</span><span class="token punctuation">;</span> 
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>\u4EE5\u4E0A\u8868\u8FBE\u5F0F\u8981\u6C42\u8BA1\u7B97\u89C4\u5219\u5E94\u7528\u4E8E\u56DB\u79CD\u4E0D\u540C\u7EC4\u5408\u4E2D. \u6211\u4EEC\u53EF\u4EE5\u7528\u4E00\u5F20\u56FE\u8868, \u6811\u7684\u5F62\u5F0F\u6765\u5448\u73B0\u8FD9\u4E9B\u7EC4\u5408, \u5982\u4E0B\u56FE.</p><p><img src="`+l+'" alt="img"></p><p>\u6BCF\u4E2A\u7EC4\u5408\u90FD\u7531\u4E00\u4E2A\u5305\u542B\u5206\u652F\u7684\u8282\u70B9\u6765\u8868\u793A, \u540C\u65F6\u6BCF\u4E2A\u5206\u652F\u53C8\u7531\u8FD0\u7B97\u7B26\u548C\u5BF9\u5E94\u7684\u8FD0\u7B97\u5143\u6784\u6210. \u7EC8\u672B\u8282\u70B9(\u4E5F\u5C31\u662F\u6CA1\u6709\u5206\u652F\u7684\u8282\u70B9)\u8981\u4E48\u662F\u8FD0\u7B97\u7B26, \u8981\u4E48\u662F\u6570\u5B57. \u4ECE\u6811\u7684\u89D2\u5EA6\u89C2\u5BDF\u8FD9\u4E9B\u8BA1\u7B97\u8FC7\u7A0B, \u6211\u4EEC\u4ECE\u7EC8\u672B\u8282\u70B9\u5F00\u59CB\u8FDB\u884C\u4E00\u6B65\u4E00\u6B65\u5411\u4E0A\u8BA1\u7B97. \u901A\u5E38\u60C5\u51B5\u4E0B, \u9012\u5F52\u662F\u4E00\u4E2A\u5F88\u6709\u7528\u7684\u5904\u7406\u5305\u542B\u5C42\u7EA7, \u6811\u72B6\u7684\u5BF9\u8C61. \u4E8B\u5B9E\u4E0A, &quot;percolate values upward&quot; \u5F62\u5F0F\u7684\u8BA1\u7B97\u89C4\u5219\u662F\u4E00\u79CD\u5E38\u7528\u7684\u8BA1\u7B97\u5F62\u5F0F, \u88AB\u79F0\u4E3A <em>tree accumulation</em>.</p><p>\u63A5\u4E0B\u6765, \u6211\u4EEC\u5F00\u59CB\u89C2\u5BDF\u7B2C\u4E00\u6B65\u7684\u91CD\u590D\u5E94\u7528</p><p>\u91CD\u70B9\u8981\u6CE8\u610F\u7684\u4E00\u70B9\u662F\u5728\u51B3\u5B9A\u8868\u8FBE\u5F0F\u4E2D\u540D\u79F0\u7684\u542B\u4E49\u65F6, \u73AF\u5883\u8D77\u7740\u91CD\u8981\u4F5C\u7528. \u5728\u7C7B\u4F3C JavaScript \u7684\u4EA4\u4E92\u578B\u8BED\u8A00\u4E2D, \u5BF9\u4E8E <code>x + 1</code> \u8FD9\u6837\u7684\u8868\u8FBE\u5F0F, \u4E0D\u63D0\u524D\u58F0\u660E\u73AF\u5883\u800C\u53BB\u8BA8\u8BBA\u8868\u8FBE\u5F0F\u7684\u503C\u662F\u5B8C\u5168\u6CA1\u6709\u610F\u4E49\u7684, \u56E0\u4E3A\u6CA1\u6709\u58F0\u660E\u73AF\u5883\u6211\u4EEC\u4E5F\u5C31\u65E0\u6CD5\u77E5\u9053 <code>x</code> \u7684\u503C. \u5728\u7B2C\u4E09\u7AE0\u4E2D\u6211\u4EEC\u4F1A\u770B\u5230, \u73AF\u5883\u7684\u542B\u4E49, \u5C31\u662F\u8BA1\u7B97\u8FC7\u7A0B\u6267\u884C\u7684\u4E0A\u4E0B\u6587, \u7406\u89E3\u73AF\u5883\u5BF9\u6211\u4EEC\u7406\u89E3\u7A0B\u5E8F\u7684\u6267\u884C\u6709\u5F88\u91CD\u8981\u7684\u610F\u4E49.</p><p>\u6CE8\u610F\u4EE5\u4E0A\u63D0\u5230\u7684\u8BA1\u7B97\u89C4\u5219\u5E76\u6CA1\u6709\u5904\u7406\u58F0\u660E. \u6BD4\u5982, \u8BA1\u7B97 <code>const x = 3;</code> \u5E76\u4E0D\u662F\u5C06 <code>=</code> \u8FD0\u7B97\u7B26\u5E94\u7528\u5230\u4E24\u4E2A\u53C2\u6570\u4E2D, \u5176\u4E2D\u4E00\u4E2A\u53C2\u6570\u662F <code>x</code>, \u53E6\u4E00\u4E2A\u662F <code>3</code>, \u4E0D\u80FD\u8FD9\u6837\u7406\u89E3\u8FD9\u4E2A\u58F0\u660E. \u5B9E\u9645\u4E0A\u8FD9\u4E2A\u58F0\u660E\u7684\u542B\u4E49\u662F\u5C06 <code>x</code> \u53D8\u91CF\u4E0E\u503C <code>3</code> \u5173\u8054. (\u4E5F\u5C31\u662F\u8BF4, <code>const x = 3;</code> \u5E76\u4E0D\u662F\u4E00\u9879\u8868\u8FBE\u5F0F\u7EC4\u5408.)</p><p><code>const</code> \u5728 JavaScript \u4E2D\u662F <strong>\u5173\u952E\u8BCD</strong> (<em>keyword</em>). \u5173\u952E\u8BCD\u6709\u7279\u6B8A\u542B\u4E49, \u56E0\u6B64\u4E0D\u80FD\u4F5C\u4E3A\u53D8\u91CF\u540D\u79F0. \u5173\u952E\u8BCD\u548C\u58F0\u660E\u4E2D\u5173\u952E\u8BCD\u7684\u7EC4\u5408\u4F1A\u544A\u8BC9 JavaScript \u89E3\u91CA\u5668, \u7528\u7279\u6B8A\u7684\u65B9\u5F0F\u5904\u7406\u8FD9\u7C7B\u58F0\u660E. \u6BCF\u4E00\u4E2A\u8FD9\u7C7B<strong>\u53E5\u6CD5</strong>(<em>syntactic</em>)\u5F62\u5F0F\u90FD\u6709\u5404\u81EA\u7684\u8BA1\u7B97\u89C4\u5219. \u4E0D\u540C\u79CD\u7C7B\u7684\u58F0\u660E\u548C\u8868\u8FBE\u5F0F(\u4EE5\u53CA\u6240\u5173\u8054\u7684\u8BA1\u7B97\u89C4\u5219), \u6784\u6210\u4E86\u7F16\u7A0B\u8BED\u8A00\u7684\u8BED\u6CD5.</p>',14);function k(v,g){const s=o("ExternalLinkIcon");return t(),c(p,null,[u,e("p",null,[e("a",m,[_,r(s)])]),h],64)}var f=n(d,[["render",k],["__file","1.1.3.evaluating-operator-combinations.html.vue"]]);export{f as default};
