import{_ as i,r,o as c,c as o,a as e,b as n,F as s,d as a,e as p}from"./app.07e6cbcb.js";const l={},h=e("h1",{id:"_1-\u5229\u7528\u51FD\u6570\u6784\u9020\u62BD\u8C61",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#_1-\u5229\u7528\u51FD\u6570\u6784\u9020\u62BD\u8C61","aria-hidden":"true"},"#"),a(" 1. \u5229\u7528\u51FD\u6570\u6784\u9020\u62BD\u8C61")],-1),d={href:"https://sourceacademy.org/sicpjs/1",target:"_blank",rel:"noopener noreferrer"},v=a("Original"),m=p('<div class="custom-container tip"><p class="custom-container-title">Quote</p><p>The acts of the mind, wherein it exerts its power over simple ideas, are chiefly these three: 1. Combining several simple ideas into one compound one, and thus all complex ideas are made. 2. The second is bringing two ideas, whether simple or complex, together, and setting them by one another so as to take a view of them at once, without uniting them into one, by which it gets all its ideas of relations. 3. The third is separating them from all other ideas that accompany them in their real existence: this is called abstraction, and thus all its general ideas are made.</p><ul><li>John Locke, An Essay Concerning Human Understanding (1690)</li></ul><p>\u5FC3\u667A, \u4E3B\u8981\u901A\u8FC7\u8FD9\u4E09\u4E2A\u65B9\u9762, \u5728\u7B80\u5355\u7684\u6982\u5FF5\u4E0B\u53D1\u6325\u4E86\u5B83\u7684\u529B\u91CF:</p><ol><li>\u5C06\u591A\u4E2A\u7B80\u5355\u7684\u60F3\u6CD5\u7EC4\u5408\u6210\u4E00\u4E2A, \u4FC3\u6210\u4E86\u590D\u6742\u60F3\u6CD5\u7684\u5B9E\u73B0.</li><li>\u5C06\u4E24\u4E2A\u4E0D\u540C\u7684\u60F3\u6CD5(\u7B80\u5355\u6216\u590D\u6742)\u8FDB\u884C\u7EC4\u5408,</li></ol></div><p>\u6211\u4EEC\u5373\u5C06\u5F00\u59CB\u5B66\u4E60<strong>\u8BA1\u7B97\u8FC7\u7A0B</strong>\u7684\u6982\u5FF5. \u8BA1\u7B97\u8FC7\u7A0B\u662F\u8BA1\u7B97\u673A\u4E2D\u7684\u62BD\u8C61\u6982\u5FF5. \u968F\u7740\u8BA1\u7B97\u8FC7\u7A0B\u8FD9\u4E2A\u6982\u5FF5\u7684\u9010\u6B65\u53D1\u5C55, \u5B83\u5F00\u59CB\u80FD\u591F\u63A7\u5236\u5176\u4ED6\u7684\u62BD\u8C61\u6982\u5FF5: <strong>\u6570\u636E</strong>. \u8BA1\u7B97\u8FC7\u7A0B\u7684\u53D1\u5C55\u9075\u5FAA\u4E00\u7CFB\u5217\u7684\u89C4\u5219, \u6211\u4EEC\u5C06\u8FD9\u4E9B\u89C4\u5219\u53EB\u505A<strong>\u7A0B\u5E8F</strong>. \u6211\u4EEC\u521B\u5EFA\u7A0B\u5E8F\u6765\u6307\u5BFC\u8BA1\u7B97\u8FC7\u7A0B. \u76F8\u5F53\u4E8E\u6211\u4EEC\u7528\u81EA\u5DF1\u7684\u5492\u8BED\u53EC\u5524\u51FA\u8BA1\u7B97\u673A\u7684\u7075\u9B42.</p><p>\u8BA1\u7B97\u8FC7\u7A0B\u5B9E\u9645\u4E0A\u8DDF\u5DEB\u5E08\u7684\u7075\u9B42\u5F88\u76F8\u4F3C. \u5B83\u770B\u4E0D\u89C1\u6478\u4E0D\u7740, \u5E76\u4E0D\u662F\u5B9E\u9645\u7684\u7269\u8D28. \u4F46\u662F\u8BA1\u7B97\u8FC7\u7A0B\u5728\u67D0\u4E9B\u65B9\u9762\u5374\u6709\u5341\u5206\u771F\u5B9E. \u5B83\u4ECE\u4E8B\u7684\u662F\u8111\u529B\u5DE5\u4F5C, \u80FD\u591F\u89E3\u7B54\u5404\u79CD\u95EE\u9898, \u80FD\u591F\u901A\u8FC7\u5B9E\u73B0\u5728\u94F6\u884C\u4E2D\u7684\u652F\u4ED8\u5DE5\u4F5C\u6216\u8005\u63A7\u5236\u5DE5\u5382\u4E2D\u673A\u5668\u4EBA\u7684\u624B\u7ED9\u6574\u4E2A\u4E16\u754C\u5E26\u6765\u5F71\u54CD. \u7528\u4E8E\u5B9E\u73B0\u8BA1\u7B97\u8FC7\u7A0B\u7684\u7A0B\u5E8F\u5C31\u50CF\u662F\u5DEB\u5E08\u7684\u5492\u8BED. \u7A0B\u5E8F\u7531\u795E\u79D8\u6666\u6DA9\u7684<strong>\u7F16\u7A0B\u8BED\u8A00</strong>\u6240\u521B\u9020\u7684\u4E00\u7CFB\u5217\u62BD\u8C61\u7684\u8868\u8FBE\u5F0F\u7EC4\u5408\u800C\u6210, \u8FD9\u4E9B\u8868\u8FBE\u5F0F\u89C4\u5B9A\u4E86\u4E00\u4E9B\u4EFB\u52A1, \u6211\u4EEC\u5E0C\u671B\u8BA1\u7B97\u8FC7\u7A0B\u53BB\u6267\u884C\u7684\u4EFB\u52A1.</p><p>\u5728\u4E00\u4E2A\u529F\u80FD\u6B63\u5E38\u7684\u8BA1\u7B97\u673A\u4E2D, \u8BA1\u7B97\u8FC7\u7A0B\u80FD\u591F\u51C6\u786E\u5730\u6267\u884C\u7A0B\u5E8F. \u5C31\u50CF\u5DEB\u5E08\u7684\u5B66\u5F92\u4E00\u6837, \u65B0\u624B\u7A0B\u5E8F\u5458\u5FC5\u987B\u5B66\u4F1A\u7406\u89E3, \u5B66\u4F1A\u9884\u6D4B\u4ED6\u4EEC\u6267\u884C\u5492\u672F\u4E4B\u540E\u7684\u7ED3\u679C. \u5728\u7A0B\u5E8F\u4E2D, \u5373\u4FBF\u662F\u5F88\u5C0F\u7684\u9519\u8BEF(\u5E38\u88AB\u79F0\u4E3A<strong>\u7F3A\u9677</strong>(<em>bug</em>)), \u4E5F\u6709\u53EF\u80FD\u5E26\u6765\u590D\u6742\u7684\u4E0D\u53EF\u9884\u6D4B\u7684\u540E\u679C.</p><p>\u4E0D\u8FC7\u5E78\u8FD0\u7684\u662F, \u5B66\u4E60\u7F16\u7A0B\u7684\u5371\u9669\u6BD4\u5B66\u4E60\u5492\u672F\u5C0F\u591A\u4E86, because the spirits we deal with are conveniently contained in a secure way. \u5C3D\u7BA1\u5982\u6B64, \u771F\u5B9E\u4E16\u754C\u7684\u7F16\u7A0B\u4F9D\u7136\u8981\u6C42\u7A0B\u5E8F\u5458\u8BA4\u771F, \u4E13\u4E1A\u4EE5\u53CA\u6709\u667A\u6167. \u5BF9\u4E8E\u4E00\u4E2A\u8BA1\u7B97\u673A\u8F85\u52A9\u8BBE\u8BA1\u7684\u7A0B\u5E8F\u6765\u8BF4, \u5373\u4F7F\u5176\u4E2D\u53EA\u5B58\u5728\u4E00\u4E2A\u5C0F\u5C0F\u7684\u7F3A\u9677, \u4E5F\u6709\u53EF\u80FD\u4F1A\u9020\u6210\u6BC1\u706D\u6027\u7684\u540E\u679C. \u6BD4\u5982\u98DE\u673A\u6545\u969C, \u5824\u575D\u574D\u584C, \u5DE5\u4E1A\u673A\u5668\u4EBA\u81EA\u6BC1\u7B49.</p><p>\u8D44\u6DF1\u7684\u8F6F\u4EF6\u5DE5\u7A0B\u5E08\u6709\u8BBE\u8BA1\u7A0B\u5E8F\u7684\u80FD\u529B, \u56E0\u6B64\u80FD\u591F\u5408\u7406\u5730\u786E\u4FDD\u8BA1\u7B97\u8FC7\u7A0B\u7684\u6700\u7EC8\u7ED3\u679C\u80FD\u591F\u5B8C\u6210\u4EFB\u52A1. \u4ED6\u4EEC\u80FD\u591F\u63D0\u524D\u5F62\u8C61\u5316\u7CFB\u7EDF\u7684\u884C\u4E3A, \u77E5\u9053\u5982\u4F55\u5BF9\u7A0B\u5E8F\u8FDB\u884C\u8BBE\u8BA1\u4EE5\u907F\u514D\u96BE\u4EE5\u9884\u671F\u7684, \u4F1A\u5E26\u6765\u707E\u96BE\u6027\u540E\u679C\u7684\u95EE\u9898\u53D1\u751F. \u5E76\u4E14\u5982\u679C\u95EE\u9898\u53D1\u751F, \u4ED6\u4EEC\u6709\u80FD\u529B\u5BF9\u7A0B\u5E8F\u8FDB\u884C\u8C03\u8BD5. \u8BBE\u8BA1\u7CBE\u5DE7\u7684\u8BA1\u7B97\u578B\u7CFB\u7EDF, \u6BD4\u5982\u6C7D\u8F66\u6216\u8005\u6838\u53CD\u5E94\u5668, \u8BBE\u8BA1\u7406\u5FF5\u90FD\u9075\u5FAA\u7EC4\u5408\u5F0F\u8BBE\u8BA1, \u56E0\u6B64\u5404\u4E2A\u90E8\u4EF6\u90FD\u53EF\u88AB\u66FF\u4EE3, \u4E5F\u66F4\u5BB9\u6613\u8C03\u8BD5.</p><h2 id="javascript-\u4E2D\u7684\u7F16\u7A0B" tabindex="-1"><a class="header-anchor" href="#javascript-\u4E2D\u7684\u7F16\u7A0B" aria-hidden="true">#</a> JavaScript \u4E2D\u7684\u7F16\u7A0B</h2><p>\u6211\u4EEC\u9700\u8981\u5408\u9002\u7684\u8BED\u8A00\u6765\u63CF\u8FF0\u8BA1\u7B97\u8FC7\u7A0B, \u672C\u4E66\u4E2D\u6211\u4EEC\u4F7F\u7528\u7684\u7F16\u7A0B\u8BED\u8A00\u662F JavaScript. \u6B63\u5982\u6211\u4EEC\u65E5\u5E38\u7684\u60F3\u6CD5\u90FD\u662F\u7528\u81EA\u7136\u8BED\u8A00(\u6BD4\u5982\u82F1\u8BED, \u745E\u5178\u8BED, \u4E2D\u6587\u7B49)\u6765\u8868\u8FBE, \u91CF\u5316\u5206\u6790\u7684\u63CF\u8FF0\u7528\u6570\u5B66\u7B26\u53F7\u6765\u8868\u793A, \u8BA1\u7B97\u8FC7\u7A0B\u7684\u8868\u8FBE\u4F7F\u7528\u7684\u662F JavaScript. JavaScript \u5728 1995 \u5E74\u88AB\u8BBE\u8BA1\u51FA\u6765, \u4EE5\u4E00\u79CD\u5185\u5D4C\u7F51\u9875\u7684\u811A\u672C\u5F62\u5F0F\u63A7\u5236\u4E86\u6D4F\u89C8\u5668\u7684\u884C\u4E3A. JavaScript \u7684\u8BBE\u8BA1\u8005\u662F Brendan Eich. \u6700\u5F00\u59CB\u5B83\u7684\u540D\u5B57\u662F <code>Mocha</code>, \u4E4B\u540E\u88AB\u91CD\u547D\u540D\u4E3A <code>LiveScript</code>, \u6700\u540E\u662F JavaScript. \u7532\u9AA8\u6587\u62E5\u6709 JavaScript \u7684\u5546\u6807\u6743.</p><p>\u5C3D\u7BA1\u6700\u5F00\u59CB\u7684\u8BBE\u8BA1\u610F\u56FE\u662F\u63A7\u5236\u7F51\u9875, \u4F46 JavaScript \u76EE\u524D\u5DF2\u7ECF\u662F\u4E2A\u901A\u7528\u7684\u7F16\u7A0B\u8BED\u8A00\u4E86. JavaScript <em>\u89E3\u91CA\u5668</em> \u7684\u529F\u80FD, \u662F\u6267\u884C\u7528 JavaScript \u8BED\u8A00\u63CF\u8FF0\u7684\u8BA1\u7B97\u8FC7\u7A0B. \u7B2C\u4E00\u4E2A JavaScript \u89E3\u91CA\u5668\u662F Eich \u5728\u7F51\u666F\u5DE5\u4F5C\u65F6\u5B9E\u73B0\u7684, \u5F53\u65F6\u88AB\u7528\u4E8E\u7F51\u666F\u6D4F\u89C8\u5668\u4E2D. JavaScript \u7EE7\u627F\u4E86 Scheme \u8BED\u8A00\u548C Self \u8BED\u8A00\u7684\u6838\u5FC3\u7279\u6027. Scheme \u662F Lisp \u7684\u4E00\u79CD\u65B9\u8A00, \u6700\u521D\u7248\u672C\u7684\u672C\u4E66\u5C31\u662F\u7528 Lisp \u8BED\u8A00\u5448\u73B0\u7684. JavaScript \u4ECE Scheme \u4E2D\u7EE7\u627F\u4E86\u6700\u57FA\u7840\u7684\u8BBE\u8BA1\u539F\u5219, \u6BD4\u5982\u8BCD\u6CD5\u4F5C\u7528\u57DF, \u4E00\u7B49\u51FD\u6570, \u52A8\u6001\u7C7B\u578B\u7B49.</p><p>JavaScript \u4E0E Java \u5C3D\u7BA1\u547D\u540D\u7C7B\u4F3C, \u4F46\u76F8\u4F3C\u4E4B\u5904\u5176\u5B9E\u5F88\u5C11, \u4E24\u8005\u90FD\u4F7F\u7528\u4E86 C \u8BED\u8A00\u7684\u5757\u7ED3\u6784. \u4F46 Java \u548C C, which usually employ compilation to lower-level languages, JavaScript \u7A0B\u5E8F\u6700\u5F00\u59CB\u5219\u662F\u7531 web \u6D4F\u89C8\u5668\u6240\u89E3\u91CA\u7684. \u5728\u7F51\u666F\u4E4B\u540E, \u4E0D\u65AD\u6709\u5176\u5B83\u6D4F\u89C8\u5668\u63D0\u4F9B\u4E86\u5BF9 JavaScript \u7684\u89E3\u91CA\u529F\u80FD, \u8FD9\u4E9B\u6D4F\u89C8\u5668\u5305\u62EC\u5FAE\u8F6F\u7684 IE, \u5F53\u65F6\u90A3\u4E2A\u7248\u672C\u7684 JavaScript \u540D\u5B57\u4E3A <code>JScript</code>. JavaScript \u5728\u63A7\u5236\u6D4F\u89C8\u5668\u8FD9\u65B9\u9762\u7684\u7206\u706B\u4F7F\u5F97\u4E3A JavaScript \u547D\u540D\u7684\u6807\u51C6\u8BDE\u751F, \u8FD9\u4E2A\u6807\u51C6\u5C31\u662F <code>ECMAScript</code>. \u521D\u7248\u672C\u7684 ECMAScript \u6807\u51C6\u5236\u5B9A\u5DE5\u4F5C\u7531 Guy Lewis Steele Jr. \u53D1\u8D77, 1997\u5E746\u6708\u7ED3\u675F, \u5F53\u65F6\u7248\u672C\u7684\u540D\u79F0\u4E3A ECMA 1997. \u7B2C\u516D\u7248, \u4E5F\u5C31\u662F ECMAScript 2015, \u5219\u662F\u7531 Allen Wirfs-Brock \u53D1\u8D77, \u5728 2015 \u5E74 6 \u6708\u88AB ECMA \u8054\u5408\u5927\u4F1A\u6240\u91C7\u7528, \u7248\u672C\u540D\u79F0\u4E3A ECMA 2015.</p><p>\u5728\u7F51\u9875\u4E2D\u5D4C\u5165 JavaScript \u7A0B\u5E8F\u7684\u5B9E\u8DF5\u6FC0\u52B1\u4E86\u6D4F\u89C8\u5668\u5F00\u53D1\u8005\u53BB\u5B9E\u73B0 JavaScript \u89E3\u91CA\u5668. \u968F\u7740\u7A0B\u5E8F\u8D8A\u6765\u8D8A\u590D\u6742, \u89E3\u91CA\u5668\u7684\u6267\u884C\u6548\u7387\u4E5F\u8D8A\u6765\u8D8A\u9AD8, \u5F00\u53D1\u8005\u4E0D\u65AD\u5F00\u53D1\u51FA\u66F4\u590D\u6742\u7684\u7F16\u8BD1\u6280\u672F, \u6BD4\u5982 JIT(\u5373\u65F6\u7F16\u8BD1). \u5927\u90E8\u5206 JavaScript \u7A0B\u5E8F(\u5728\u672C\u6587\u7F16\u8457\u7684\u65F6\u95F4 2021 \u5E74)\u90FD\u5185\u5D4C\u5728\u7F51\u9875\u4E2D\u5E76\u4E14\u88AB\u6D4F\u89C8\u5668\u6240\u89E3\u91CA, \u4E0D\u8FC7\u4E0E\u6B64\u540C\u65F6, JavaScript \u8FD8\u9010\u6E10\u6269\u5927\u4E86\u5E94\u7528\u8303\u56F4, \u53D8\u6210\u4E86\u4E00\u4E2A\u6BD4\u8F83\u901A\u7528\u7684\u7F16\u7A0B\u8BED\u8A00, \u5728\u670D\u52A1\u5668\u7AEF\u9002\u7528, Node.js \u5C31\u662F\u4E00\u4E2A\u793A\u4F8B.</p><p>\u7136\u800C, \u662F\u6D4F\u89C8\u5668\u6267\u884C JavaScript \u7A0B\u5E8F\u7684\u80FD\u529B\u4F7F\u5F97 JavaScript \u53D8\u6210\u6700\u5408\u9002\u7684\u7F16\u5199\u7EBF\u4E0A\u7248\u672C\u8BA1\u7B97\u673A\u7A0B\u5E8F\u4E66\u7684\u7F16\u7A0B\u8BED\u8A00. \u5728\u7F51\u9875\u4E2D\u901A\u8FC7\u70B9\u51FB\u7684\u65B9\u5F0F\u6267\u884C\u7A0B\u5E8F\u5728 JavaScript \u4E2D\u662F\u5F88\u5E38\u89C1\u7684\u529F\u80FD, \u5176\u5B9E\u8FD9\u70B9\u6BCB\u5EB8\u7F6E\u7591, \u6BD5\u7ADF JavaScript \u5C31\u662F\u88AB\u8BBE\u8BA1\u51FA\u6765\u5E72\u8FD9\u4E8B\u7684. \u66F4\u91CD\u8981\u7684\u662F, ES2015 \u8FD8\u62E5\u6709\u4E00\u7CFB\u5217\u7279\u6027\u4F7F\u5F97 JavaScript \u6210\u4E3A\u5B66\u4E60\u91CD\u8981\u7F16\u7A0B\u6982\u5FF5\u548C\u6570\u636E\u7ED3\u6784\u7684\u4F18\u79C0\u5A92\u4ECB, \u540C\u65F6\u4E5F\u5341\u5206\u9002\u5408\u901A\u8FC7\u8FD9\u4E9B\u6982\u5FF5\u6765\u9610\u8FF0\u4E0E\u4E4B\u76F8\u5173\u7684\u8BED\u8A00\u7279\u6027. JavaScript \u7684\u8BCD\u6CD5\u4F5C\u7528\u57DF, \u4E00\u7B49\u516C\u6C11\u51FD\u6570, and their syntactic support through lambda expressions provide direct and concise access to functional abstraction, and dynamic typing allows the adaptation to remain close to the Scheme original throughout the book. \u7531\u4E8E\u4EE5\u4E0A\u63D0\u53CA\u6216\u8005\u672A\u63D0\u53CA\u7684\u79CD\u79CD\u539F\u56E0, JavaScript \u7F16\u7A0B\u5C31\u53D8\u5F97\u5341\u5206\u6709\u8DA3\u4E86.</p>',12);function S(J,u){const t=r("ExternalLinkIcon");return c(),o(s,null,[h,e("p",null,[e("a",d,[v,n(t)])]),m],64)}var _=i(l,[["render",S],["__file","1.abstraction-with-functions.html.vue"]]);export{_ as default};
